var fipsAllowedSignatureAlgorithms = []SignatureAlgorithm{
	SHA256WithRSAPSS,
	SHA384WithRSAPSS,
	SHA512WithRSAPSS,
	SHA256WithRSA,
	ECDSAWithSHA256,
	SHA384WithRSA,
	ECDSAWithSHA384,
	SHA512WithRSA,
	ECDSAWithSHA512,
}

func isFipsAllowedSignatureAlgorithm(sigAlg SignatureAlgorithm) bool {
	for _, s := range fipsAllowedSignatureAlgorithms {
		if s == sigAlg {
			return true
		}
	}
	return false
}

